import React from "react";
import ReactDOM from "react-dom";
import classes from "./CartModal.module.css";

const CartModal = (props) => {
  const Backdrop = (props) => {
    return <div classes={classes.backdrop} />;
  };

  const OverLay = (props) => {
    return (
      <div classes={classes.modal}>
        <div>{props.children}</div>
      </div>
    );
  };
  const portEle = document.getElementById("overlays");

  return (
    <React.Fragment>
      {ReactDOM.createPortal(<Backdrop />, portEle)}
      {ReactDOM.createPortal(<OverLay>{props.children}</OverLay>, portEle)}
    </React.Fragment>
  );
};

export default CartModal;
