import React, { useState } from "react";
import { Link, Outlet, BrowserRouter, Routes, Route, Switch } from "react-router-dom";
import { useSelector } from "react-redux";
import LocationPage from "../Pages/LocationPage";
import HomePage from "../Pages/HomePage";
import OrderPage from "../Pages/OrderPage";
import MenuPage from "../Pages/MenuPage";
import Cart from "../Cart/Cart";
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";
import { uiActions } from "../../store/ui-slice";

const Header = (props) => {
  const showCart = useSelector((state) => state.ui.cartIsVisible);

  const [closeCartModal, setCloseCartModal] = useState(false);

  const cartClickHandler = () => {
    setCloseCartModal(true);
  };

  const closeCartClickHandler = () => {
    setCloseCartModal(false);
  };

  return (
    <div className={classes.header}>
      {showCart && <Cart onClick={closeCartClickHandler} />}
      <nav>
        <ul>
          <Link to="/">
            <li>Home</li>
          </Link>

          <Link to="menuPage">
            <li>Menu</li>
          </Link>

        </ul>
      </nav>

      <HeaderCartButton />


      <Switch>
        <Route path="/" element={<HomePage/>}/>
        <Route path="/menuPage" element={<MenuPage />} />        
      </Switch>
    </div>
  );
};

export default Header;
